import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-observable',
  templateUrl: './observable.component.html',
  styles: []
})
export class ObservableComponent implements OnInit {

  // 宣告 Observable
  source$: Observable<number>;

  // 初始化資料流
  constructor() {
    // 創建一個新的 Observable 並訂閱
    this.source$ = new Observable(subscriber => {
      console.log('資料流開始'); // 輸出資料流開始訊息
      subscriber.next(1); // 發送值 1
      subscriber.next(2); // 發送值 2
      subscriber.next(3); // 發送值 3
      setTimeout(() => {
        subscriber.next(4);
        subscriber.complete();
        console.log('stream 結束');
      });
      console.log('資料流結束'); // 輸出資料流結束訊息
      subscriber.complete(); // 完成資料流
    });
  }

  // 初始化元件
  ngOnInit() {
    // 訂閱 Observable
    this.source$.subscribe({
      next: data => console.log(`Observable 第一次訂閱: ${data}`), // 輸出接收到的資料
      complete: () => console.log('Observable 第一次訂閱完成') // 輸出訂閱完成訊息
    });
    this.source$.subscribe({
      next: data => console.log(`Observable 第二次訂閱: ${data}`),
      complete: () => console.log('Observable 第二次訂閱完成')
    });
  }
}
